package p160_162;

public class example5_2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i = 4; i > 0; i--) {
			for(int j = 1 ;  j <5; j++) {
				if(j <= i) {
				System.out.print("*");
				}else {
					System.out.print(" ");
				}
			}
			System.out.println();
		}

	}

}
