package p160_162;

public class example05_2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i = 4; i > 0; i--) {
			for(int j = 1 ;  j <5; j++) {
				System.out.print("");
				if(j <= i) {
				System.out.print("*");
				}
			}
			System.out.println();
		}
	}
}




